import {useState} from "react";
import "./App.css";

function App() {
  const list = [
    {id: 1, name: "Київ"},
    {id: 2, name: "Львів"},
    {id: 3, name: "Харків"},
    {id: 4, name: "Одеса"},
  ];

  const [index, setIndex] = useState();

  const handleClick = () => {
    setIndex(index + 1)
  };

  return (
    <div className="App">
      <header className="App-header">
        <button onClick={handleClick}>Next</button>
        <h1>Current city: {list[index].name}</h1>
      </header>
    </div>
  );
}

export default App;
