import {useState} from "react";
import "./App.css";

const getInitialColor = (length) => {
  if(length > 3) return 1;
  return 0;
}

function App() {
  const list = [
    {id: 1, name: "Київ"},
    {id: 2, name: "Львів"},
    {id: 3, name: "Харків"},
    {id: 4, name: "Одеса"},
  ];

  const color = ['red', 'blue', 'green'];

  const [index, setIndex] = useState(0);

  const [colorIndex, setColorIndex] = useState(() => getInitialColor(list.length));

  const handleClick = () => {
    setIndex((prevIndex) => prevIndex === list.length -1 ? 0: prevIndex + 1);
  };

  const handleClickColor = () => {
    setColorIndex(colorIndex + 1);
    console.log()
  }

  console.log('render', index, colorIndex)

  return (
    <div className="App">
      <header className="App-header">
      <button onClick={handleClick}>Next city</button>
      <button onClick={handleClickColor}>Next color</button>
        <h1 style={{color: color[colorIndex]}}>Current city: {list[index].name}</h1>
      </header>
    </div>
  );
}

export default App;
